import React, { Component } from "react";
import axios from "axios";

const API_KEY = "afbd007707mshf3e391b451ab77cp1daa16jsn8b3241a3e170";

class GenerateStats extends Component {
  constructor() {
    super();
    this.state = {
      loading: true,
      stats: [],
      gamertag: "reubsz92",
      platform: "psn",
    };
  }

  componentDidMount() {
    const { gamertag, platform } = this.state;

    this.setState({ loading: false });
    fetch(
      `https://call-of-duty-modern-warfare.p.rapidapi.com/warzone/${gamertag}/${platform}`,
      {
        method: "GET",
        headers: {
          "x-rapidapi-host": "call-of-duty-modern-warfare.p.rapidapi.com",
          "x-rapidapi-key": API_KEY,
        },
      }
    )
      .then((response) => response.json())
      .then((data) => {
        console.log(data);
        this.setState({
          stats: data,
        });
      });
  }
  render() {
    let myData = this.state.stats.br || {};
    let wins = myData.wins;
    let gamesPlayed = myData.gamesPlayed;
    let winPcent = (wins / gamesPlayed) * 100;
    let scorePerMin = myData.scorePerMinute;
    if (this.state.loading) {
      return <div>Loading...</div>;
    } else
      return (
        <div>
          <p>stats</p>
          <p>Your wins are: {myData.wins}</p>
          <p>games played: {myData.gamesPlayed}</p>
          <p>Your win percentage is: {winPcent.toFixed(2)}%</p>
          <p>Top Fives: {myData.topFive}</p>
          <p>Top Tens: {myData.topTen}</p>
          <p>Top Twenty Fives: {myData.topTwentyFive}</p>
          <p>Kill/Death Ratio: {myData.kdRatio}</p>
          <p>Downs: {myData.downs}</p>
          <p>Kills: {myData.kills}</p>
          <p>Deaths: {myData.deaths}</p>
          <p>Revives: {myData.revives}</p>
          <p>Score: {myData.score}</p>
          <p>Score Per Min: {scorePerMin.toFixed(2)}</p>
        </div>
      );
  }
}

export default GenerateStats;
