import React, { Component } from "react";
import Stats from "./stats";
import Header from "./header";
import Footer from "./footer";

const API_KEY = "afbd007707mshf3e391b451ab77cp1daa16jsn8b3241a3e170";

class GenerateStats extends Component {
  constructor() {
    super();
    this.state = {
      loading: true,
      stats: [],
      gamertag: "reubsz92",
      platform: "psn",
    };

    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleChange = this.handleChange.bind(this);
  }

  componentDidMount() {
    const { gamertag, platform } = this.state;

    this.setState({ loading: false });
    fetch(
      `https://call-of-duty-modern-warfare.p.rapidapi.com/warzone/${gamertag}/${platform}`,
      {
        method: "GET",
        headers: {
          "x-rapidapi-host": "call-of-duty-modern-warfare.p.rapidapi.com",
          "x-rapidapi-key": API_KEY,
        },
      }
    )
      .then((response) => response.json())
      .then((data) => {
        console.log(data);
        this.setState({
          stats: data,
        });
      });
  }

  handleChange(event) {
    event.preventDefault();
    this.setState(
      { gamertag: event.target.value },
      { platform: event.target.value }
    );

    //const { name, value } = event.target;
    //this.setState({
    // [name]: value,
    //});
  }

  handleSubmit(event) {
    event.preventDefault();
    var gt = this.state.gamertag;
    var pf = this.state.platform;
    this.setState({ gt });
    this.setState({ pf });
  }

  render() {
    let myData = this.state.stats.br || {};
    if (this.state.loading) {
      return <div>Loading...</div>;
    } else
      return (
        <div>
          <Header info={myData} />
          <Stats info={myData} />
          <Footer />
        </div>
      );
  }
}

export default GenerateStats;
