import React, { Component } from "react";
import Stats from "./stats";
import Header from "./header.component";
import Footer from "./footer";

const API_KEY = "afbd007707mshf3e391b451ab77cp1daa16jsn8b3241a3e170";

class GenerateStats extends Component {
  constructor() {
    super();
    this.state = {
      loading: true,
      stats: [],
      gamertag: undefined,
      platform: undefined,
      error: false,
    };
  }

  getStats = async (e) => {
    e.preventDefault();

    const gamertag = e.target.elements.gamertag.value;
    const platform = e.target.elements.platform.value;

    if (gamertag && platform) {
      const api_call = await fetch(
        `https://call-of-duty-modern-warfare.p.rapidapi.com/warzone/${gamertag}/${platform}`,
        {
          method: "GET",
          headers: {
            "x-rapidapi-host": "call-of-duty-modern-warfare.p.rapidapi.com",
            "x-rapidapi-key": API_KEY,
          },
        }
      );
      const response = await api_call.json();

      console.log(response);

      this.setState({
        stats: response,
        gamertag: this.state.gamertag,
        platform: this.state.platform,
        error: false,
      });
    } else {
      this.setState({ error: true });
    }
  };

  render() {
    let myData = this.state.stats.br || {};
    return (
      <div>
        <Header getStats={this.getStats} error={this.state.error} />
        <Stats info={myData} states={this.state} />
        <Footer />
      </div>
    );
  }
}

export default GenerateStats;
